import { useState } from 'react'
import { Container, Row, Col } from 'react-bootstrap';

const BlogCreator = () => {

    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');

    const style = {
        padding: '20px',
    }


    const handleSubmit = (e) => {
        e.preventDefault();
        const blog = { title: title, content: content };
        console.log(blog);
    }
    return (
        <div>
            <form onSubmit={handleSubmit}>
                <Container>
                    <Row>
                        <Col>
                            <input style={style} type="text" value={title} onChange={(e) => { setTitle(e.target.value) }} />
                        </Col>
                        <Col>
                            <textarea style={style} value={content} onChange={(e) => { setContent(e.target.value) }} />
                        </Col>
                    </Row>


                    <Row>
                        <center>
                            <input type="submit" value='POST' />
                        </center>
                    </Row>
                </Container>
            </form>
        </div>
    )
}

export default BlogCreator
